import React from "react";

const About = ()=> {
    return (
        <div className="container mx-auto py-10">
            <h2 className="text-3xl font-bold text-center">About This Website</h2>
            <p className="mt-4 text-lg">This website is dedicated to providing high-quality academic writing services tailored to students' needs. 
        From research papers and essays to case studies and reports, we strive to deliver work that exceeds expectations 
        and helps you achieve academic success. Explore our services, browse our portfolio, and see how we can assist 
        you with your assignments.
        </p>

        </div>
    )
}


export default About;
